import React from "react";
import { connect } from "react-redux";
import classNames from "classnames";
import { setActiveComponent, restart } from "../actions";
import Histogram from "./Histogram";

// show the number of questions asked and the favourite car/remaining cars.
function Result({
  labels,
  candidates,
  leftPoints,
  attributes,
  mask,
  numLeftPoints,
  restart
}) {
  let ths = [];
  attributes.forEach(([attr, config]) => {
    if (mask[attr]) {
      const th = <th key={attr}>{attr}</th>;
      ths.push(th);
    }
  });
  const trs = leftPoints.map((idx, i) => (
    <tr key={i} data-toggle="tooltip" title={labels[idx]}>
      {candidates[idx].map((x, j) => (
        <td key={j}>{x}</td>
      ))}
    </tr>
  ));
  return (
    <div className="justify-content-center">
      <h4>
        {leftPoints.length === 1
          ? "Your Favourite Car is:"
          : `${leftPoints.length} Cars Left in the Database:`}
      </h4>
      <table
        className={classNames("table", "table-hover", {
          "table-fixed": trs.length > 7
        })}
        style={{
          maxWidth: "40rem",
          margin: "auto"
        }}
      >
        <thead>
          <tr>{ths}</tr>
        </thead>
        <tbody>{trs}</tbody>
      </table>
      <div className="d-flex justify-content-center my-3">
        <Histogram />
      </div>
      <div>
        <button type="button" className="btn btn-primary" onClick={restart}>
          Return to Welcome
        </button>
      </div>
    </div>
  );
}

const mapStateToProps = ({
  labels,
  candidates,
  leftPoints,
  attributes,
  mask,
  numLeftPoints
}) => ({
  labels,
  candidates,
  leftPoints,
  attributes,
  mask,
  numLeftPoints
});

const mapDispatchToProps = dispatch => ({
  restart: () => {
    dispatch(setActiveComponent("Welcome"));
    dispatch(restart());
  }
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Result);
