import React from "react";
import { connect } from "react-redux";
import classNames from "classnames";
import { setActiveComponent, restart } from "../actions";
import Histogram from "./Histogram";

// show the number of questions asked and the favourite car/remaining cars.
function Result({
  labels,
  candidates,
  leftPoints,
  attributes,
  mask,
  numLeftPoints,
  restart
}) {
  let ths = [];
  attributes.forEach(([attr, config]) => {
    if (mask[attr]) {
      const th = <th key={attr}>{attr}</th>;
      ths.push(th);
    }
  });
  const trs = leftPoints.map((idx, i) => (
    <tr key={i} data-toggle="tooltip" title={labels[idx]}>
      {candidates[idx].map((x, j) => (
        <td key={j}>{x}</td>
      ))}
    </tr>
  ));

  const top1 = [];
  for(let i = 0; i < 1; ++i)
      top1.push(trs[i]);

  return (
    <div className="justify-content-center">
      <h4>
      Here is your favorite car.
      </h4>
      <table
        className={classNames("table", "table-hover", {
          "table-fixed": trs.length > 7
        })}
        style={{
          maxWidth: "40rem",
          margin: "auto"
        }}
      >
        <thead>
          <tr>{ths}</tr>
        </thead>
        <tbody>{top1}</tbody>
      </table>
    </div>
  );
}

const mapStateToProps = ({
  labels,
  candidates,
  leftPoints,
  attributes,
  mask,
  numLeftPoints
}) => ({
  labels,
  candidates,
  leftPoints,
  attributes,
  mask,
  numLeftPoints
});

const mapDispatchToProps = dispatch => ({
  restart: () => {
    dispatch(setActiveComponent("Welcome"));
    dispatch(restart());
  }
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Result);
