import React from "react";
import { connect } from "react-redux";
import classNames from "classnames";
import { setActiveComponent, restart } from "../actions";
import Histogram from "./Histogram";

class Result extends React.Component {
  constructor(props) {
    super(props);
    this.ths = [];
    this.props.attributes.forEach(([attr, config]) => {
      if (this.props.mask[attr]) {
        const th = <th key={attr}>{attr}</th>;
        this.ths.push(th);
      }
    });
    this.ths.push(<th key="chooseCheckbox"/>);

    this.trs = this.props.leftPoints.map((idx, i) => (
        <tr key={i} data-toggle="tooltip" title={this.props.labels[idx]}>
          {this.props.candidates[idx].map((x, j) => (
              <td key={j}>{x}</td>
          ))}
          <td key="chooseCheckbox"/>
        </tr>
    ));

    const sss = this.props.scores.map((sid, i) => (
        <tr key={i} data-toggle="tooltip" title={this.props.labels[i]}>
          <td key={i}>{sid}</td>
        </tr>
    ));

    //top-1 points
    const top1 = []; top1.push(this.trs[0]);
    //obtain the index
    const top1small = [];
    const top1large = [];
    for (let i = 1; i < this.props.leftPoints.length; ++i) {
      if ((this.props.scores[0] - this.props.scores[i]) / this.props.scores[0] < 0.1)
        top1small.push(this.props.leftPoints[i]);
      else
        top1large.push(this.props.leftPoints[i]);
    }

    const top1smallSample = this.pickRandom(top1small, 5);
    const top1largeSample = this.pickRandom(top1large, 5);
    for (let i = 0; i < top1largeSample.length; ++i)
      top1smallSample.push(top1largeSample[i]);
    const top1Samplewithbox = top1smallSample.map((idx, i) => (
        <tr key={i} data-toggle="tooltip" title={this.props.labels[idx]}>
          {this.props.candidates[idx].map((x, j) => (
              <td key={j}>{x}</td>
          ))}
          <td>
            <input type="checkbox" id={`myCheckbox${i}`} name={`myCheckbox${i}`} value="myValue"/>
            <label htmlFor={`myCheckbox${i}`}> Prefer </label>
          </td>
        </tr>
    ));

    const present = [];
    for(let i = 0; i < this.props.leftPoints.length; ++i)
    {
      present.push(this.props.candidates[this.props.leftPoints[i]].map((x, j) => {
        return (x);
      }));
      present.push(this.props.scores[i]);
      present.push("\n");
    }

    this.state = {
      intro: "Here is your favorite car.",
      inst: "Please compare your favorite car and the following cars <br/> and indicate if you are interested in the following cars?",
      ths: this.ths,
      top: top1,
      topSamplewithbox: top1Samplewithbox,
      output: present
    };
  }

  pickRandom = (arr, n) => {
    const arrCopy = [...arr];
    if (arrCopy.length <= n)
      return arrCopy;
    // Shuffle array
    for (let i = arrCopy.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [arrCopy[i], arrCopy[j]] = [arrCopy[j], arrCopy[i]]; // ES6 array element swap
    }
    return arrCopy.slice(0, n);
  };

  downloadFile = index => {
    if (index === 1) {
      //top-5 points
      const top5 = [];
      for(let i = 0; i < 5; ++i)
        top5.push(this.trs[i]);
      //obtain the index
      const top5small = [];
      const top5large = [];
      for (let i = 1; i < this.props.leftPoints.length; ++i) {
        if ((this.props.scores[0] - this.props.scores[i]) / this.props.scores[0] < 0.1)
          top5small.push(this.props.leftPoints[i]);
        else
          top5large.push(this.props.leftPoints[i]);
      }

      const top5smallSample = this.pickRandom(top5small, 5);
      const top5largeSample = this.pickRandom(top5large, 5);
      for (let i = 0; i < top5largeSample.length; ++i)
        top5smallSample.push(top5largeSample[i]);
      const top5Samplewithbox = top5smallSample.map((idx, i) => (
          <tr key={i} data-toggle="tooltip" title={this.props.labels[idx]}>
            {this.props.candidates[idx].map((x, j) => (
                <td key={j}>{x}</td>
            ))}
            <td>
              <input type="checkbox" id={`myCheckbox${i}`} name={`myCheckbox${i}`} value="myValue"/>
              <label htmlFor={`myCheckbox${i}`}> Prefer </label>
            </td>
          </tr>
      ));
      this.setState({
        intro: "Here are your top-5 favorite cars.",
        inst: "Please compare your favorite top-5 cars and the following cars <br/> and indicate if you are interested in the following cars?",
        top: top5,
        topSamplewithbox: top5Samplewithbox
      });
    }
    else if(index === 5) {
      //top-5 points
      const top10 = [];
      for (let i = 0; i < 10; ++i)
        top10.push(this.trs[i]);
      //obtain the index
      const top10small = [];
      const top10large = [];
      for (let i = 1; i < this.props.leftPoints.length; ++i) {
        if ((this.props.scores[0] - this.props.scores[i]) / this.props.scores[0] < 0.1)
          top10small.push(this.props.leftPoints[i]);
        else
          top10large.push(this.props.leftPoints[i]);
      }

      const top10smallSample = this.pickRandom(top10small, 5);
      const top10largeSample = this.pickRandom(top10large, 5);
      for (let i = 0; i < top10largeSample.length; ++i)
        top10smallSample.push(top10largeSample[i]);
      const top10Samplewithbox = top10smallSample.map((idx, i) => (
          <tr key={i} data-toggle="tooltip" title={this.props.labels[idx]}>
            {this.props.candidates[idx].map((x, j) => (
                <td key={j}>{x}</td>
            ))}
            <td>
              <input type="checkbox" id={`myCheckbox${i}`} name={`myCheckbox${i}`} value="myValue"/>
              <label htmlFor={`myCheckbox${i}`}> Prefer </label>
            </td>
          </tr>
      ));
      this.setState({
        intro: "Here are your top-10 favorite cars.",
